<?phpcetak_gambar(5);function cetak_gambar($total){    if($total%2 == 0){        echo('Hanya input bilangan ganjil!');    }else{         for($i=1; $i<=$total; $i++){        for($j=1; $j<=$total; $j++){            if($j == 1 || $j == $total || $i == (floor($total/2)+1)){                echo '* ';            }else{                echo '= ';            }        }                echo "\r\n";    }       }}